module Main where

import Test.Tasty
import Test.Tasty.HUnit

import qualified Convert as C
import qualified SimpleRSA as S

main = defaultMain $ testGroup "3. Ãœbungsblatt" [exercise1, exercise2]

exercise1 = testGroup "Convert" $ [
    testCase "convertToBase 10 1984" $ 
      C.convertToBase 10 1984 @?= [4,8,9,1] 
  , testCase "convertToBase 8 1984" $ 
      C.convertToBase 8 1984 @?= [0,0,7,3] 
  , testCase "convertFromBase 8 [0,0,7,3]" $
      C.convertFromBase 8 [0,0,7,3] @?= 1984
  , testCase "convertFromBase 2 [1, 1, 0, 1]" $
      C.convertFromBase 2 [1, 1, 0, 1] @?= 11
  , testCase "stringAsNumber \"foo\"" $
      C.stringAsNumber "foo" @?= 7303014
  , testCase "numberAsString 7496034" $
      C.numberAsString 7496034 @?= "bar"
  ]

exercise2 = testGroup "SimpleRSA" $ [
    testCase "encrypt prk64 123456789" $ 
      S.encrypt S.prk64 1234567890 @?= 6020575960829543985
  , testCase "decrypt S.pbk64 6020575960829543985" $
      S.decrypt S.pbk64 6020575960829543985 @?= 1234567890
  , testCase "decryptMsg pbk128 (encryptMsg prk128 \"Foobaz?\")" $
     S.decryptMsg S.pbk128 (S.encryptMsg S.prk128 "Foobaz?") @?= "Foobaz?"
  , testCase "chunkLength prk1024" $
     S.chunkLength S.prk1024 @?= 127
  , testCase "decryptMsg pbk2048 (encryptMsg prk2048 msg)" $
     S.decryptMsg S.pbk2048 (S.encryptMsg S.prk2048 msg) @?= msg
  ]


